import React, {createRef} from "react";
import "./BlogCard.scss";

export default function BlogCard({blog, isDark}) {
  const imgRef = createRef();

  function openUrlInNewTab(url) {
    if (url !== undefined) {
      var win = window.open(url, "_blank");
      win.focus();
    }
  }

  const GetDescBullets = ({descBullets, isDark}) => {
    return descBullets
      ? descBullets.map((item, i) => (
          <li
            key={i}
            className={isDark ? "dark-mode-text" : "subTitle"}
          >
            {item}
          </li>
        ))
      : null;
  };

  return (
    <div onClick={() => openUrlInNewTab(blog.url)}>
      <div className={isDark ? "blog-container dark-mode" : "blog-container"}>
        <a
          className={
            isDark ? "dark-mode blog-card blog-card-shadow" : "blog-card"
          }
          href="#blog"
        >
        <img
          crossOrigin={"anonymous"}
          ref={imgRef}
          className="blog-roundedimg"
          src={blog.image}
          alt={blog.imageName}
        />
          <h3 className={isDark ? "small-dark blog-title" : "blog-title"}>
            {blog.title}
          </h3>
          <p className={isDark ? "small-dark blog-date" : "blog-date"}>
            {blog.date}
          </p>
          <p className={isDark ? "small-dark small" : "small"}>
            {blog.description}
          </p>
          <div className="go-corner">
            <div className="go-arrow">â†’</div>
          </div>
          <ul className="blog-descBullets">
            <GetDescBullets descBullets={blog.descBullets} isDark={isDark} />
          </ul>
        </a>
      </div>
    </div>
  );
}
